var mongoose = require("mongoose");

var carSchema = mongoose.Schema({
	brand : {
		type : String,
		required: true
	},
	model : {
		type : String,
		required: true
	},
	year : {
		type : String,
		required: true
	},
	create_date : {
		type : Date,
		default: Date.now
	}
});

var Car = module.exports = mongoose.model("Car",carSchema);

// GET cars

module.exports.getCars  = function(callback,limit){
	Car.find(callback).limit(limit);
};

module.exports.getCarById  = function(id,callback){
	Car.findById(id,callback);
};